import edu.duke.*;

public class Part1 {
    int findStopCodon(String dna, int startIndex, String stopCodon){
        int result = -1;
        int currIndex = dna.indexOf(stopCodon, startIndex+3);
        while(currIndex != -1){
            if((currIndex-startIndex) % 3 == 0){
                return currIndex;
            }
            else{
                currIndex = dna.indexOf(stopCodon, currIndex+1);
            }
        }
        return result;
    }
    
    String findGene (String dna, int where){
        int startIndex = dna.indexOf("atg", where);
        if(startIndex == -1){
            return "";
        }
        int taaIndex = findStopCodon(dna,startIndex,"taa");
        int tagIndex = findStopCodon(dna,startIndex,"tag");
        int tgaIndex = findStopCodon(dna,startIndex,"tga");
        int minIndex = 0;
        if(taaIndex == -1 || (tgaIndex != -1 && (tgaIndex < taaIndex))){
            minIndex = tgaIndex;
        }
        else{
            minIndex = taaIndex;
        }
        if(minIndex == -1 || (tagIndex != -1 && (tagIndex < minIndex))){
            minIndex = tagIndex;
            }
        if(minIndex == -1){
            return "";
        }
        
        return dna.substring(startIndex,minIndex+3);
    }
    
    void PrintAllGenes (String dnaStr){
        int position = 0;
        String dna = dnaStr;
        String currentGene = "";
        while(true){
            //The only time a gene should not be found is when
            //there is no more gene to be found
            currentGene = findGene(dna, position);
            if(currentGene.isEmpty()){
                break;
            }
            System.out.println(currentGene);
            position = dna.indexOf(currentGene, position) 
                            + currentGene.length();
        }
    }
    
    void testPrintAllGenes(){
        String dna = "atgtag";
        dna = dna.toLowerCase();
        PrintAllGenes(dna);
    }
    
    void testPrintAllGenes(String dna){
        PrintAllGenes(dna);
    }
    
    StorageResource getAllGenes (String dnaStr){
        StorageResource sr = new StorageResource();
        int position = 0;
        String dna = dnaStr;
        String currentGene = "";
        while(true){
            currentGene = findGene(dna, position);
            if(currentGene.isEmpty()){
                break;
            }
            sr.add(currentGene);
            position = dna.indexOf(currentGene, position) 
                            + currentGene.length();
        }
        return sr;
    }
    
    double cgRatio(String dna){
        int result = 0;
        int noOfCGs = 0;
        for(char ch : dna.toCharArray()){
            if(ch == 'c' || ch == 'g'){
                noOfCGs++;
            }
        }
        return ((double) noOfCGs)/dna.length();
    }
    
    void testCgRatio (){
        String dna = "";
        double answer = cgRatio(dna);
        System.out.println(answer);
    }
    
    int ctCTG(String dna){
        int result = 0;
        if(dna.indexOf("CTG")!= -1){
            return -1;
        }
        int r = 0;
        while(true){
            int pos = dna.indexOf("CTG", r);
            if(pos != -1){
                result++;
                r = pos+1;
            }
            else{
                break;
            }
        }
        return result;
    }
    void processGenes(StorageResource sr){
        StorageResource grSixty = new StorageResource();
        int countGrSixty = 0;
        StorageResource cgRatioGr = new StorageResource();
        int countCgRatioGr = 0;
        int longestGene = 0;
        for(String s : sr.data()){
            //get genes that length is greater than 60
            if(s.length()>60){
                grSixty.add(s);
                countGrSixty++;
            }
            //get genes that cgRatio is greater than 0.35
            if(cgRatio(s)>0.35){
                cgRatioGr.add(s);
                countCgRatioGr++;
            }
            //get longest gene
            if(s.length()>longestGene){
                longestGene = s.length();
            }
        }
        //Print Results
        System.out.println("GENES GREATER THAN 60 : ");
        for(String s : grSixty.data()){
            System.out.println(s);
        }
        System.out.println("GENES GREATER THAN 60 : "+countGrSixty);
        System.out.println("C/G GREATER THAN 0.35 : ");
        for(String s : cgRatioGr.data()){
            System.out.println(s);
        }
        System.out.println("The number of C/G GREATER THAN 0.35 : "+countCgRatioGr);
        System.out.println("Longest Gene Length : "+longestGene);
    }
    
    void testProcessGenes(){
        //Get DNA file via File Resource from Directory
        FileResource fr = new FileResource("GRch38dnapart.fa");
        //Convert file into a string
        String dna = fr.asString();
        //convert dna to lowercase just in case
        dna = dna.toLowerCase();
        //Retrieve all the genes in the dna string
        StorageResource sr = getAllGenes(dna);
        //Process all the genes found, via processGenes method
        processGenes(sr);
    }
    
    void countCTG(){
        //Get DNA file via File Resource from Directory
        FileResource fr = new FileResource("GRch38dnapart.fa");
        //Convert file into a string
        String dna = fr.asString();
        //convert dna to lowercase just in case
        //dna = dna.toLowerCase();
        int ctg = ctCTG(dna);
        System.out.println("no of ctg is : " + ctg);
    }
}
